'use strict';
var execa = require('execa');
var executable = require('executable');
module.exports = function(bin, args) {
    if (!Array.isArray(args)) {
        args = [
            '--help'
        ];
    }
    return executable(bin).then(function(works) {
        if (!works) {
            throw new Error("Couldn't execute the `".concat(bin, "` binary. Make sure it has the right permissions."));
        }
        return execa(bin, args);
    }).then(function(res) {
        return res.code === 0;
    });
};
module.exports.sync = function(bin, args) {
    if (!Array.isArray(args)) {
        args = [
            '--help'
        ];
    }
    if (!executable.sync(bin)) {
        throw new Error("Couldn't execute the `".concat(bin, "` binary. Make sure it has the right permissions."));
    }
    return execa.sync(bin, args).status === 0;
};
