'use strict';
var extList = require('ext-list');
var sortKeysLength = require('sort-keys-length');
module.exports = function(str) {
    var obj = sortKeysLength.desc(extList());
    var exts = Object.keys(obj).filter(function(x) {
        return str.endsWith(x);
    });
    if (exts.length === 0) {
        return [];
    }
    return exts.map(function(x) {
        return {
            ext: x,
            mime: obj[x]
        };
    });
};
module.exports.mime = function(str) {
    var obj = sortKeysLength.desc(extList());
    var exts = Object.keys(obj).filter(function(x) {
        return obj[x] === str;
    });
    if (exts.length === 0) {
        return [];
    }
    return exts.map(function(x) {
        return {
            ext: x,
            mime: obj[x]
        };
    });
};
